mapper.select:
select * from shopping_cart

         and name = #{name} 
         and dish_id = #{dishId} 
         and setmeal_id = #{setmealId} 
         and user_id = #{userId} 
         and dish_flavor = #{dishFlavor}

mapper.update:
update shopping_cart
name = #{name}, image = #{image}, dish_id = #{dishId}, setmeal_id = #{setmealId}, user_id = #{userId}, dish_flavor = #{dishFlavor}, number = #{number}, amount = #{amount}, create_time = #{createTime},
where id = #{id}

mapper.delete:
delete from shopping_cart

     and dish_id = #{dishId} 
     and setmeal_id = #{setmealId} 
     and user_id = #{userId} 
     and dish_flavor = #{dishFlavor}
